const rootEl=document.documentElement,modalButtons=()=>document.querySelectorAll("[data-toggle]"),isCloseButton=t=>"close"===t.target.attributes["aria-label"].value,openModal=t=>e=>{rootEl.classList.add("is-clipped"),t.classList.add("is-active")},closeModal=t=>e=>{isCloseButton(e)&&(t.classList.remove("is-active"),rootEl.classList.remove("is-clipped"))},addModalListener=t=>{const e=t.attributes["data-target"].value,o=document.getElementById(e);t.addEventListener("click",openModal(o)),o.addEventListener("click",closeModal(o))};modalButtons().forEach(addModalListener);